package Operators;

public class Inverted_operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a= -100;
int b= 100;
boolean c=true;
System.out.println(!c);
System.out.println(~a);//~(-ve)value the value will become +ve by decreasing 1 number
System.out.println(~b);//~(+VE)value the value will become -ve by decresing 1 number
	}

}
