package Operators;

public class Shifted_operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/* shifted operators two types
		 * 1.left shifted
		 * 2.right shifted
		 */
		System.out.println(10<<4); //left shifter 10(2^4)
		System.out.println(10>>4); //right shifter 10(2^4)
		System.out.println (10<<8);
		System.out.println (10>>6);
		

	}

}
