package Operators;

public class unary_operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int a=10;
      //incrementing (++)
      //Decrementing
      System.out.println(a);
      System.out.println(a++);
      System.out.println(a);
      System.out.println(a++);
      System.out.println(a);
      System.out.println(++a);
	}

}
